import { Link } from 'react-router-dom'
import '../css/loginpage.css'
import axios from 'axios'
import { useEffect, useState } from 'react'
async function getUser() {
    try {
        const response = await axios.get('http://127.0.0.1:8000/users/');
        return response.data;
    } catch (error) {
        console.error(error);
    }
}
function LoginPage() {
    const [user, setUser] = useState([]);
    useEffect(() => {
        getUser().then(data => {
            setUser(data);
        });
    }, []);
    const [phone, setPhone] = useState('');
    const [pass, setPass] = useState('');
    const [code, setCode] = useState('');
    function ConfirmLogin(result){
        setPhone(document.getElementById("phone").value);
        setPass(document.getElementById("pass").value);
        setCode(document.getElementById("code").value);
        user.map((item, index) => {
            if(item.phone === phone && item.password === pass && result === code){
                window.location.replace('/wellcome');
            }
            else{
                alert("Sai tài khoản hoặc mã xác nhận");
                window.location.replace('/login');
            }
        })
    }
    var characters = 'abcdefghijklmnopqrstuvwxyz0123456789';
    var result = ""
    var chaactersLength = characters.length;

    for ( var i = 0; i < 6 ; i++ ) {
        result += characters.charAt(Math.floor(Math.random() * chaactersLength));
    }

    return (
        <div className="login-page">
            <div className="logo">
                <img className='homelogo' src='./img/homelogo.png' alt="logo" />
                <h2>Smart Home</h2>
            </div>
            <div className="dangnhap">
                <div className="title">
                    <h1>Đăng nhập</h1>
                </div>
                <form className="input-form">
                    <div className="sdt">
                        <p>Số điện thoại:</p>
                        <input className='input' type='number' id='phone'/>
                    </div>
                    <div className="sdt">
                        <p>Mật khẩu:</p>
                        <input className='input' type='password' id='pass'/>
                    </div>
                    <div className="sdt">
                        <p>Mã xác nhận:</p>
                        <div className='input'>
                            <div className="code">{result}</div>
                            <input className='code-input' type='text'  id='code'/>
                        </div>
                    </div>
                    <div className="button">
                        <Link className='btn' to="/"><button>Hủy bỏ</button></Link>
                        <div className='btn' ><button onClick={()=>ConfirmLogin(result)} type='submit'>Xác nhận</button></div>
                    </div>
                    <div className='text'>
                        <Link to="forgot-password">Quên mật khẩu</Link>
                        <p>Bạn chưa có tài khoản? <a href="mailto:danhauloc@gmail.com">Liên hệ</a> với chúng tôi</p>
                    </div>
                </form>
            </div>
        </div>
    )
}
export default LoginPage;
